mapper.update:
UPDATE `member`
SET	nickname = #{nickname},
	phone = #{tel},
	email = #{email}
WHERE	memberId = #{memberId}

UPDATE loginInfo
SET	PASSWORD = #{password}
WHERE	member_id = #{member_id}

mapper.select:
SELECT	*
FROM	`loginInfo`
WHERE	member_id = #{member_id}
AND	PASSWORD = #{password}

mapper.update:
	update review
    SET status = #{status}
    where reviewid = #{reviewId}
