mapper.select:
SELECT r.reviewId ,r.regDate ,r.updateDate ,r.status ,r.score ,r.body ,r.foodPhoto ,r.reply ,r.writerid ,m.nickname FROM review r, `member` m WHERE r.writerid = m.memberId AND r.status != 'N' AND r.store_id = #{storeId}
ORDER BY r.score DESC
ORDER BY r.regDate DESC
LIMIT #{startLimit}, #{endLimit}

SELECT COUNT(*) AS result
FROM review
WHERE store_id = #{storeId}

SELECT ROUND(AVG(score),1)
FROM review
WHERE store_id = #{storeId}

mapper.update:
update review
SET reply = #{reply}
where reviewId = #{reviewId}

update review
SET status = #{status}
where reviewid = #{reviewId}
