mapper.select:
SELECT regDate, s.`name` storename, s.`status` as status, address as address, m.`name` membername, s.storeId storeId FROM store s, `member` m WHERE s.owner_id = m.memberId
AND s.`status` = #{option}
ORDER BY regDate DESC LIMIT #{startLimit}, #{endLimit}

SELECT COUNT(*) AS result
FROM store

    WHERE status = #{option}

mapper.update:
UPDATE store
SET STATUS = #{status}
WHERE storeId = #{storeId}

mapper.select:
SELECT fo.regDate regDate, fo.`name` foodname, fo.`status` as status, fo.`description` as description, fo.price as price, fi.`name` filename, fo.foodId foodId FROM food fo, food_file fi WHERE fo.foodId = fi.food_id AND fo.store_id = #{store_id}
AND fo.`status` = #{option}
ORDER BY fo.regDate DESC LIMIT #{startLimit}, #{endLimit}

SELECT COUNT(*) AS result
FROM food
WHERE store_id = #{store_id}

    AND status = #{option}

SELECT *, ( ROUND(6371 * ACOS( COS( RADIANS(#{lat}) ) * COS( RADIANS( s.localy ) ) * COS( RADIANS( s.localx ) - RADIANS( #{lon}) ) + SIN( RADIANS(#{lat}) ) * SIN( RADIANS( s.localy ) ) ), 2)) AS distance FROM store s, ( SELECT fileUrl, store_id FROM ( SELECT name fileUrl, store_id, ROW_NUMBER() OVER (PARTITION BY store_id ORDER BY regDate DESC) as rankNo FROM store_file ) t WHERE rankNo = 1 ) f WHERE s.storeId = f.store_id
AND s.storeKind LIKE CONCAT('%', #{options}, '%')
AND s.status = 'Y'
HAVING distance < #{radius}
ORDER BY distance LIMIT 0,#{qantity};

mapper.update:
UPDATE food
SET updateDate = NOW(),
    `name`= #{foodname},
    `description` = #{desc},
    `status` = #{status}
WHERE foodId = #{foodId}
